package review.java;

public class VarDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//定义两个整型变量x,y
		int x=0,y=3;
		x=3;
		y=0;
		System.out.println("x="+x);
		System.out.println("y="+y);
		
		//换行语句1
		System.out.println();
		System.out.println("x="+x);
		
		//换行语句2
		System.out.print(x+" "+y+"\n");
		System.out.print("sample text");
		
		

	}

}
